
import { Component, Input, OnInit } from '@angular/core';
import { faFileLines } from '@fortawesome/free-solid-svg-icons/faFileLines';
import { Prescription } from '../../../../../bean/models/prescription';
import { CustodiaService } from '../../../../../services/helpers/custodia/custodia.service';
import { CardModule } from 'primeng/card';
import { PrescriptionLabeledElementComponent } from '../prescription-labeled-element/prescription-labeled-element.component';

@Component({
  selector: 'sacyl-prescription-general-data',
  standalone: true,
  imports: [ CardModule, PrescriptionLabeledElementComponent ],
  templateUrl: './prescription-general-data.component.html',
  styleUrls: ['./prescription-general-data.component.css']
})
export class PrescriptionGeneralDataComponent implements OnInit{

  fileIcon = faFileLines;
  loader =  false;
  prescriptionStatus = '';
  @Input() prescription!: Prescription;
  encoded = '';

  constructor(private custodiaService: CustodiaService){}


  ngOnInit(): void {
      this.loader = true;
 
      console.log("Prescription status: ", this.prescription.status, this.prescription.idDocument);
      this.prescriptionStatus = this.prescription.status.code;
      
      //this.prescriptionStatus = this.prescription.status;
  }


  get type() {
    
    return (this.prescription.type.code || '') + ' ' + (this.prescription.type.display || '');
  }

  openPdf(idDocument: string){


    this.custodiaService.getDispensationDocument(idDocument).subscribe(data => {
      if (data) {
        try {
          this.openBase64PdfInNewTab(this.encoded);
        } catch (error) {
          console.log("Error al obtener documento desde custodia: ", error);
        }
      } else {
        console.log("Error idDocument undefined");
      }
    });

  }

  openBase64PdfInNewTab(base64Pdf: string, fileName = 'documento.pdf'): void {
    // Convert Base64 to Blob
    const byteCharacters: string = atob(base64Pdf);
    const byteNumbers: number[] = new Array(byteCharacters.length);
    for (let i = 0; i < byteCharacters.length; i++) {
      byteNumbers[i] = byteCharacters.charCodeAt(i);
    }
    const byteArray: Uint8Array = new Uint8Array(byteNumbers);
    const pdfBlob: Blob = new Blob([byteArray], { type: 'application/pdf' });

    // Create a data URL
    const pdfUrl: string = URL.createObjectURL(pdfBlob);

    // Open a new tab with the PDF
    const newTab = window.open(pdfUrl, '_blank');

    // Check if the new tab was successfully opened
    if (!newTab) {
      console.error('Error opening new tab. Please check your browser\'s popup settings.');
    }
  }

}
